import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, TextField, Button, Paper } from '@mui/material';
import debounce from 'lodash.debounce';

const DataTable = () => {
  const [data, setData] = useState([]);
  const [originalData, setOriginalData] = useState([]);
  const [newRow, setNewRow] = useState({
    name: '',
    address: '',
    city: '',
    religion: '',
    age: ''
  });

  useEffect(() => {
    axios.get('http://localhost:8080/api/employees')
      .then(response => {
        setData(response.data);
        setOriginalData(response.data);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  // Function to check if there are changes
  const hasChanges = () => {
    return JSON.stringify(data) !== JSON.stringify(originalData);
  };

  // Function to check if data is substantially changed
  const isSubstantiallyChanged = (newData) => {
    return JSON.stringify(newData) !== JSON.stringify(originalData);
  };

  // Function to save changes
  const saveData = async () => {
    if (!hasChanges()) return; // Do not save if there are no changes

    try {
      const updatedData = [...data, newRow];
      if (isSubstantiallyChanged(updatedData)) {
        await axios.patch('http://localhost:8080/api/employees/batch', updatedData);
        setOriginalData(data);
        console.log('Data saved successfully!');
      } else {
        console.log('No significant changes detected.');
      }
    } catch (error) {
      console.error('Error saving data:', error);
    }
  };

  // Debounced save function
  const debouncedSaveData = useCallback(debounce(saveData, 1000), [data]);

  useEffect(() => {
    debouncedSaveData();
  }, [data, debouncedSaveData]);

  const handleChange = (e, rowIndex, columnId) => {
    const newData = [...data];
    newData[rowIndex][columnId] = e.target.value;
    setData(newData);
  };

  const handleNewRowChange = (e, columnId) => {
    setNewRow({
      ...newRow,
      [columnId]: e.target.value
    });
  };

  const handleDelete = (id) => {
    const updatedData = data.filter(row => row.id !== id);
    axios.patch('http://localhost:8080/api/employees/batch', updatedData)
      .then(response => {
        setData(updatedData);
        setOriginalData(updatedData);
        console.log('Data deleted successfully!');
      })
      .catch(error => console.error('Error deleting data:', error));
  };

  return (
    <div>
      <TableContainer component={Paper} style={{ marginTop: '20px' }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Name</TableCell>
              <TableCell>Address</TableCell>
              <TableCell>City</TableCell>
              <TableCell>Religion</TableCell>
              <TableCell>Age</TableCell>
              <TableCell>Action</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((row, rowIndex) => (
              <TableRow key={row.id}>
                <TableCell>{row.id}</TableCell>
                <TableCell>
                  <TextField
                    value={row.name}
                    onChange={(e) => handleChange(e, rowIndex, 'name')}
                    variant="outlined"
                    size="small"
                    fullWidth
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    value={row.address}
                    onChange={(e) => handleChange(e, rowIndex, 'address')}
                    variant="outlined"
                    size="small"
                    fullWidth
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    value={row.city}
                    onChange={(e) => handleChange(e, rowIndex, 'city')}
                    variant="outlined"
                    size="small"
                    fullWidth
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    value={row.religion}
                    onChange={(e) => handleChange(e, rowIndex, 'religion')}
                    variant="outlined"
                    size="small"
                    fullWidth
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    value={row.age}
                    onChange={(e) => handleChange(e, rowIndex, 'age')}
                    variant="outlined"
                    size="small"
                    fullWidth
                    type="number"
                  />
                </TableCell>
                <TableCell>
                  <Button
                    variant="contained"
                    color="secondary"
                    onClick={() => handleDelete(row.id)}
                    size="small"
                  >
                    Delete
                  </Button>
                </TableCell>
              </TableRow>
            ))}
            <TableRow>
              <TableCell>New</TableCell>
              <TableCell>
                <TextField
                  value={newRow.name}
                  onChange={(e) => handleNewRowChange(e, 'name')}
                  variant="outlined"
                  size="small"
                  fullWidth
                />
              </TableCell>
              <TableCell>
                <TextField
                  value={newRow.address}
                  onChange={(e) => handleNewRowChange(e, 'address')}
                  variant="outlined"
                  size="small"
                  fullWidth
                />
              </TableCell>
              <TableCell>
                <TextField
                  value={newRow.city}
                  onChange={(e) => handleNewRowChange(e, 'city')}
                  variant="outlined"
                  size="small"
                  fullWidth
                />
              </TableCell>
              <TableCell>
                <TextField
                  value={newRow.religion}
                  onChange={(e) => handleNewRowChange(e, 'religion')}
                  variant="outlined"
                  size="small"
                  fullWidth
                />
              </TableCell>
              <TableCell>
                <TextField
                  value={newRow.age}
                  onChange={(e) => handleNewRowChange(e, 'age')}
                  variant="outlined"
                  size="small"
                  fullWidth
                  type="number"
                />
              </TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default DataTable;
